    return (
        <ProductPageContext.Provider value={{ sort, setSort, rating, setRating, priceRange, setPriceRange, category, setCategory }}>
            {children}
        </ProductPageContext.Provider>
    );
}
export {ProductPageContext, ProductPageProvider};
